/**
 * Get timezone by value
 */
export function getTimezoneByValue(value: string): Timezone | undefined {
  return TIMEZONES.find(tz => tz.value === value)
}

/**
 * Get timezones by region
 */
export function getTimezonesByRegion(region: string): Timezone[] {
  return TIMEZONES.filter(tz => tz.region === region)
}

/**
 * Get all unique regions
 */
export function getTimezoneRegions(): string[] {
  const regions = new Set(TIMEZONES.map(tz => tz.region))
  return Array.from(regions).sort()
}

/**
 * Search timezones (by label, value, or region)
 */
export function searchTimezones(query: string): Timezone[] {
  const lowerQuery = query.toLowerCase()
  return TIMEZONES.filter(tz =>
    tz.label.toLowerCase().includes(lowerQuery) ||
    tz.value.toLowerCase().includes(lowerQuery) ||
    tz.region.toLowerCase().includes(lowerQuery) ||
    tz.abbreviation.toLowerCase().includes(lowerQuery)
  )
}

/**
 * Suggest timezone based on country name
 */
export function suggestTimezoneForCountry(countryName: string): Timezone | undefined {
  const countryTimezoneMap: Record<string, string> = {
    'United States': 'America/New_York',
    'Canada': 'America/Toronto',
    'United Kingdom': 'Europe/London',
    'Australia': 'Australia/Sydney',
    'Zimbabwe': 'Africa/Harare',
    'Zambia': 'Africa/Lusaka',
    'South Africa': 'Africa/Johannesburg',
    'Nigeria': 'Africa/Lagos',
    'Kenya': 'Africa/Nairobi',
    'India': 'Asia/Kolkata',
    'China': 'Asia/Shanghai',
    'Japan': 'Asia/Tokyo',
    'Germany': 'Europe/Berlin',
    'France': 'Europe/Paris',
    'Brazil': 'America/Sao_Paulo',
    'Mexico': 'America/Mexico_City',
    'Spain': 'Europe/Madrid',
    'Italy': 'Europe/Rome',
  }
  
  const suggestedValue = countryTimezoneMap[countryName]
  if (suggestedValue) {
    return getTimezoneByValue(suggestedValue)
  }
  return undefined
}

